create table users (
    id bigint PRIMARY KEY AUTO_INCREMENT,
    guid character varying(155),
    code character varying (255),
    name character varying (253) NOT NULL,
    phone character varying(14) NOT NULL,
    email character varying(253),
    photo clob,
    password character varying(155) NOT NULL,
    time_created bigint
);

create table user_permissions(
    user_id bigint REFERENCES users(id),
    permission character varying(55)
);

create table user_follows (
    id bigint PRIMARY KEY AUTO_INCREMENT,
    user_id bigint,
    following_id bigint
);

create table sheets (
    id bigint PRIMARY KEY AUTO_INCREMENT,
    user_id bigint,
    content text,
    sixty_four clob,
    video character varying(29),
    origin_id bigint,
    origin_content text,
    likes_count bigint,
    shares_count bigint,
    time_created bigint
);


create table hearts (
    id bigint PRIMARY KEY AUTO_INCREMENT,
    user_id bigint,
    paper_id bigint
);

